import React from 'react';
import ReactDOM from 'react-dom';
import * as script from './Script.js';
import './index.css';
import { Modal, Form, Input, Radio, Select, InputNumber } from 'antd';
import { Button } from 'antd';
import { useState } from 'react';

const ManuallyEditForm = ({ visible, onCreate, onCancel }) => {
    const [form] = Form.useForm();
    return (
        <Modal
          visible={visible}
          title="手動修改任務"
          okText="儲存"
          cancelText="取消"
          onCancel={onCancel}
          onOk={() => {
          form
            .validateFields()
            .then((values) => {
                form.resetFields();
                onCreate(values);
            })
            .catch((info) => {
                console.log('Validate Failed:', info);
            });
          }}
        >
        <Form
          form={form}
          layout="vertical"
          name="form_in_modal"
          initialValues={{
            modifier: 'public',
          }}
        >
          <Form.Item
            name="package"
            label="Package"
            rules={[
              {
                required: false,
                message: '',
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="customNo"
            label="客戶代碼"
            rules={[
              {
                required: true,
                message: '必填欄位',
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="produceBatchNo"
            label="生產批號"
            rules={[
              {
                required: true,
                message: '必填欄位',
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="workPosition"
            label="作業站"
            rules={[
              {
                required: true,
                message: '必填欄位',
              },
            ]}
          >
            <Select>
              <Select.Option value="cp1">CP1</Select.Option>
              <Select.Option value="cp2">CP2</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item
            name="productModelNo"
            label="產品型號"
            rules={[
              {
                required: true,
                message: '必填欄位',
              },
