import React from "react";
import ReactTable from "react-table-6";  
import "react-table-6/react-table.css";
import { MenuOutlined } from '@ant-design/icons';
import { sortableContainer, sortableElement, sortableHandle } from 'react-sortable-hoc';
import { Button,Table } from 'antd';
import arrayMove from 'array-move';


const DragHandle = sortableHandle(() => <MenuOutlined style={{ cursor: 'grab', color: '#999' }} />);
function changeOrderStatus(event){
	event.target.parentNode.parentNode.nextSibling.textContent = "已派工";
	event.target.parentNode.parentNode.nextSibling.style.color = "blue";
}

const SortableItem = sortableElement(props => <tr {...props} />);
const SortableContainer = sortableContainer(props => <tbody {...props} />);

class SortableTable extends React.Component {
	constructor(props){
		super(props);
		this.buffer = []
    }


	onSortEnd = ({ oldIndex, newIndex }) => {

	  if (oldIndex !== newIndex) {
		console.log(oldIndex,newIndex);
		this.buffer = arrayMove([].concat(this.buffer), oldIndex, newIndex).filter(el => !!el);
		console.log(this.buffer);
	  }
	};

	DraggableContainer = props => (
	  <SortableContainer
		useDragHandle
		disableAutoscroll
		helperClass="row-dragging"
		onSortEnd={this.onSortEnd}
		{...props}
	  />
	);

	DraggableBodyRow = ({ className, style, ...restProps }) => {
		var index = 0;

		if(this.buffer.length > 0){
			index = this.buffer.findIndex(x => x.index === restProps['data-row-key']);
		}
		return <SortableItem index={index} {...restProps} />;
	};

	render() {
		//if(!!this.props.data)
		this.buffer = this.props.data;
	  return (
		<Table
		  pagination={false}
		  dataSource={this.buffer}
		  columns={this.props.metadata}
		  rowKey="index"
		  components={{
			body: {
			  wrapper: this.DraggableContainer,
			  row: this.DraggableBodyRow,
			},
		  }}
		/>
	  );
	}
  }

